class ToggleFriendship{constructor(e){this.toggler=e,this.toggleFriendship()}toggleFriendship(){$(this.toggler).click(function(e){e.preventDefault();let i=this;$.ajax({type:"POST",url:$(i).attr("href")}).done(function(e){let t=parseInt($(i).attr("data-Friendships"));console.log(t),1==e.data.deleted?(--t,$(i).html("Add Friend")):(t+=1,$(i).html("Remove Friend")),$("#friend").html(t+" Friends"),$(i).attr("data-friendships",t)}).fail(function(e){console.log("error in completing the request")})})}}